package learningjava;

public class BitwiseOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number1=5;
		int number2=7;
		System.out.println(number1&number2);
		System.out.println(number1|number2);
		System.out.println(~number1);
		System.out.println(number1^number2);
		System.out.println(number1<<number2);
		System.out.println(number1>>number2);
	}

}
